sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	var issueId = $sp.getParameter('sys_id');

    data.issue = {};
    data.technician = null;
    data.activities = [];
	if(issueId){
			var gr = new GlideRecord('x_1850913_facili_0_facility_issue');
			if(gr.get(issueId)){
				data.issue={
					sys_id: gr.getValue('sys_id'),
                number: gr.getValue('number'),
                short_description: gr.getValue('short_description'),
                description: gr.getValue('description'),
                state: gr.getValue('state'),
                state_label: gr.getDisplayValue('state'),
                priority: gr.getValue('priority'),
                priority_label: gr.getDisplayValue('priority'),
                location: gr.getDisplayValue('locatio0'),
                issue_type: gr.getDisplayValue('issue_categor0'),
                building: gr.getDisplayValue('building'),
                opened_at: gr.getDisplayValue('sys_created_on'),
                updated_at: gr.getDisplayValue('sys_updated_on'),
                assigned_to: gr.getValue('assigned_technician')
				}

				if(gr.getValue('assigned_technician')){
					var techGr = new GlideRecord('sys_user');
					if (techGr.get(gr.getValue('assigned_technician'))){
						 var fullName = techGr.getDisplayValue('name');
             var nameParts = fullName.split(' ');
						 var initials = nameParts[0].charAt(0) + (nameParts[1] ? nameParts[1].charAt(0) : '');
						data.technician = {
                        name: fullName,
                        title: techGr.getDisplayValue('title'),
                        email: techGr.getValue('email'),
                        phone: techGr.getValue('phone'),
                        initials: initials.toUpperCase()
                    };
					}
				}
			}

	}	
})();
