sp_widget.script:
(function() {
    data.issues = [];

    var userId = gs.getUserID();
    var gr = new GlideRecord('x_1850913_facili_0_facility_issue');
    gr.addQuery('opened_by', userId);
    gr.orderByDesc('sys_created_on');
    gr.setLimit(10);
    gr.query();

    while (gr.next()) {
        data.issues.push({
            sys_id: gr.getValue('sys_id'),
            number: gr.getValue('number'),
            short_description: gr.getValue('short_description'),
            location: gr.getDisplayValue('locatio0') || gr.getDisplayValue('location'),
            state: gr.getValue('state'),
            state_label: gr.getDisplayValue('state'),
            priority: gr.getValue('priority'),
            opened_at: gr.getValue('sys_created_on')
        });
    }
})();
