sys_variable_value.value:
(function execute(inputs, outputs) {
   try{   
        //Outputs  
        outputs.records_created = 0;
        outputs.records_updated = 0;
        outputs.alerts_generated = 0;
        outputs.issues_created = 0;
        outputs.issues_updated = 0; 
        outputs.issues_closed = 0;   
        outputs.errors = 0;


        var sensorDataJson = inputs.sensor_data; // 
        if (!sensorDataJson) {
            gs.warn('No sensor data received to process');
            return;
        }

        var sensorReadings =JSON.parse(sensorDataJson);//Convert into JS object
        //Counters
        var recordsCreated = 0;
        var alertsGenerated = 0;
        var recordsUpdated = 0;
        var issuesCreated = 0;
        var issuesUpdated = 0;   
        var issuesClosed = 0;    
        var errors = 0;
        //Loop through every sensor
        for(var i = 0;i<sensorReadings.length;i++){

        var sensor = sensorReadings[i];
        try{
            //Sensor exist or new one
            var gr = new GlideRecord("x_1850913_facili_0_facility_sensor");
            gr.addQuery('sensor_id',sensor.sensor_id);
            gr.query();
            
            var isNewRecord = !gr.next();
            if(isNewRecord){
                gr.initialize();
                gr.sensor_id = sensor.sensor_id;
            }
        // gr.initialize();
        // gr.sensor_id = sensor.sensor_id;
        gr.sensor_type=sensor.sensor_type;
        gr.status = sensor.status;
        gr.location = sensor.location;
        gr.building=sensor.building;
        gr.metric_value = sensor.metric_value
        if(sensor.status=='warning'){
            var currentCount = parseInt(gr.warning_count || 0);
            gr.warning_count = currentCount + 1;
        }else if(sensor.status=='normal'||sensor.status == 'alert'){
            gr.warning_count = 0;
        }

        
        if(sensor.timestamp){
            gr.reading_timestamp = new GlideDateTime(sensor.timestamp);
        }else{
            gr.reading_timestamp = new GlideDateTime();
        }
        //Insert Or update
         var sysId;
                if (isNewRecord) {
                    sysId = gr.insert();
                    if (sysId) {
                        recordsCreated++;
                        gs.info('Created new sensor record: ' + sensor.sensor_id);
                    }
                } else {
                    sysId = gr.update();
                    if (sysId) {
                        recordsUpdated++;
                        gs.info('Updated sensor record: ' + sensor.sensor_id);
                    }
                }

        if(sysId&&sensor.status=='warning'&&gr.warning_count >= 3){
            var maintenanceAction = createMaintenanceTask(sensor, gr.sys_id.toString());
             if (maintenanceAction == 'created') {
                // Reset counter after creating task
                gr.warning_count = 0;
                gr.update();
            }

        }
        // Manage issues
        if(sysId){
            var issueAction = createOrUpdateFacilityIssue(sensor, gr.sys_id.toString());

            if(issueAction == 'created'){
                issuesCreated++;
                alertsGenerated++;
            }else if(issueAction =='updated'){
                issuesUpdated++;
                alertsGenerated++;
            }else if (issueAction == 'closed') {
                issuesClosed++;
            }
        }else{
             errors++;
            gs.error('Failed to save sensor: ' + sensor.sensor_id);
        }

        
        }catch(e){
            errors++;

            gs.error('Error processing sensor reading: ' + e.message);
        }
    
    }
    //Set Output
    outputs.records_created = recordsCreated;
    outputs.records_updated = recordsUpdated;
    outputs.alerts_generated =alertsGenerated;
    outputs.issues_created = issuesCreated;
    outputs.issues_updated = issuesUpdated; 
    outputs.issues_closed = issuesClosed; 
    outputs.errors=errors;
   }catch(error){
        gs.error('Critical error processing Mockaroo sensor data: ' + error.message);
        outputs.records_created = 0;
        outputs.records_updated = 0;
        outputs.issues_created = 0;
        outputs.issues_updated = 0;
        outputs.issues_closed = 0;
        outputs.alerts_generated = 0;
        outputs.errors = 1;
   }
   function createOrUpdateFacilityIssue(sensor , sensorSysId){
        try{
            var existingIssue = new GlideRecord('x_1850913_facili_0_facility_issue');
            existingIssue.addQuery('sensor_typ0', sensorSysId)
            existingIssue.addQuery('state', 1);
            existingIssue.query();
            var hasExisting = existingIssue.next();
            if(sensor.status == 'alert'){
                if(hasExisting)
                {
                    existingIssue.description = buildAlertDescription(sensor, sensorSysId, true);
                    existingIssue.priority = calculatePriority(sensor);
                    existingIssue.metric_value = sensor.metric_value;
                    existingIssue.last_reading
                    existingIssue.update();
                    return 'updated'; 
                }
                else
                {
                    var issue = new GlideRecord('x_1850913_facili_0_facility_issue');
                    issue.initialize();
                    issue.short_description = 'SENSOR ALERT: ' + (sensor.sensor_type || 'Unknown').toUpperCase() + ' Location - '+ sensor.location;
                    issue.description = buildAlertDescription(sensor, sensorSysId, false);
                    issue.state = 1;
                    issue.priority = calculatePriority(sensor);
                    issue.locatio0 = sensor.location;
                    issue.building = sensor.building;
                    issue.floor = sensor.location.split('-')[1];
                    issue.reported_vi0 = 'iot';
                    issue.opened_by = '09b1f1ae830efa104acac396feaad314';
                    issue.affected_device = '6e4f37398342f6104acac396feaad357';
                    issue.sensor_typ0 = sensorSysId; //Reference of the sensor 
                    var issueSysId = issue.insert();
                    if (issueSysId) {
                            gs.info('Created facility issue ' + issue.number + ' for sensor: ' + sensor.sensor_id);
                            return 'created';
                        } else {
                            gs.error('Failed to create facility issue for sensor: ' + sensor.sensor_id);
                            return 'none';
                        }
                }

            }
            else if((sensor.status === 'normal' || sensor.status === 'warning')&&hasExisting){
                    existingIssue.state = 7;
                    existingIssue.close_notes = 'Sensor has returned to ' + sensor.status.toUpperCase() + ' status.\n' +
                    'Latest Reading: ' + sensor.metric_value + '\n' +
                    'Timestamp: ' + sensor.timestamp + '\n' +
                    'Auto-closed by sensor monitoring system.';
                    existingIssue.closed_at = new GlideDateTime();
                    existingIssue.closed_by = '48df0322834a3e104acac396feaad357';
                    existingIssue.assigned_technician = '';
                    existingIssue.update();
                    return 'closed';
            
            }
            return 'none';
               
        
}catch(err){
    gs.error('Error creating facility issue: ' + err.message);
    return 'none';
}
}
function buildAlertDescription(sensor, sensorSysId, isUpdate){
        var description = 'ALERT DETAILS:\n';
        if (isUpdate) {
        description += 'UPDATED: ' + new GlideDateTime().getDisplayValue() + '\n\n';
    }
        description+='Sensor ID: ' + sensor.sensor_id + '\n';
        description += 'Type: ' + (sensor.sensor_type || 'Unknown').toUpperCase() + '\n'; 
        description += 'Location: ' + sensor.location + '\n';
        description += 'Status: ALERT\n\n';
        description += 'TIMESTAMP: ' + sensor.timestamp + '\n';

        return description;
}
function calculatePriority(sensor) {
    if (sensor.metric_value > 90) {
        return 1;  // Critical
    } else if (sensor.metric_value > 75) {
        return 2;  // High
    } else {
        return 3;  // Medium
    }
}
function createMaintenanceTask(sensor, sensorSysId) {
    try{
        var task = new GlideRecord('x_1850913_facili_0_maintenance_task');
        task.initialize();
        task.short_description = 'Maintenance Required: ' + sensor.sensor_type + ' - ' + sensor.location;
        task.description = 'MAINTENANCE REQUIRED:\n\n' +
            'Sensor has reported WARNING status 3 consecutive times.\n\n' +
            'Sensor ID: ' + sensor.sensor_id + '\n' +
            'Type: ' + (sensor.sensor_type || 'Unknown').toUpperCase() + '\n' + 'Location: ' + sensor.location + '\n' +
            'Building: ' + sensor.building + '\n' +
            'Last Metric Value: ' + sensor.metric_value + '\n' ;
        task.state = 1
        task.priority = 2;
        task.sensor = sensorSysId;
        var taskSysId = task.insert();
         if (taskSysId) {
            gs.info('Created maintenance task ' + task.number + ' for sensor: ' + sensor.sensor_id);
            return 'created';
        } else {
            gs.error('Failed to create maintenance task for sensor: ' + sensor.sensor_id);
            return 'none';
        }
    }catch(err){
        gs.error('Error creating maintenance task: ' + err.message);
        return 'none';
    }
}

})(inputs, outputs);

