sys_variable_value.value:
(function execute(inputs, outputs) {
    outputs.success = false;
    outputs.sensor_data = '';
    outputs.record_count = 0;
    outputs.error_message = '';
    try{
        /*
        1-send get request to the api 
        2-get the response 
        3-parse it 
        4- action based on the response
        */
        //1- configure the req & req header 
        var apiUrl = 'https://api.mockaroo.com/api/9b53cdc0?count=30&key=27dfe080';
        var request = new sn_ws.RESTMessageV2(); 
        request.setEndpoint(apiUrl);
        request.setHttpMethod('GET');
        request.setRequestHeader("Accept","application/json");
        
        //2- Execute req
        var response = request.execute();
        var responseBody = response.getBody(); // sensor data at JSON formate
        var statusCode = response.getStatusCode();
        gs.info('API Response - Status Code: ' + statusCode);
        
        if(statusCode == 200){
            var sensorData = JSON.parse(responseBody);
            
            if(Array.isArray(sensorData) && sensorData.length > 0){
                outputs.sensor_data = JSON.stringify(sensorData);
                //outputs.responseBody = responseBody;
                outputs.success = true; 
                outputs.record_count = sensorData.length; 
                outputs.error_message = '';
                gs.info('Sensor Data fetched: ' + sensorData.length);
                //gs.info("Sensor Data is" + sensorData[1])
            }else{
                outputs.sensor_data = '';
                outputs.success = false;
                outputs.record_count = 0;
                outputs.error_message = 'No data returned from API';
                gs.warn('Mockaroo returned empty data');
            }

        }else{
            outputs.success = false;
            outputs.error_message = 'API returned status: ' + statusCode;
            gs.error('Mockaroo API Error: ' + statusCode + ' - ' + responseBody);
        }
    }catch(err){
        outputs.success = false;
        outputs.error_message = err.message;
        gs.error('Error calling Mockaroo API: ' + err.message);
    }
 

})(inputs, outputs);

