sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    if (!current.issue_categor0.nil()) {
        return;
    }
    var category = null;
    var source = 'unknown';
    if (!current.sensor_typ0.nil()) {
        source = 'sensor';
        var sensor = current.sensor_typ0.getRefRecord();
        if (sensor) {
            var sensorType = sensor.getValue('sensor_type');
            category = mapSensorTypeToCategory(sensorType);
            gs.info('[CATEGORY] Sensor issue detected | Type: ' + sensorType + ' â†’ Category: ' + category);
        }
    }
    if (category) {
        current.issue_categor0 = category;
        gs.info('[CATEGORY] Set category to: ' + category + ' (Source: ' + source + ')');
    }

    function mapSensorTypeToCategory(sensorType) {
		if (!sensorType) {
            return 'other';
        }
        var type = sensorType.toLowerCase().trim();
        var categoryMap = {
            'temperature': 'hvac',
            'hvac': 'hvac',
            'heat': 'hvac',
            'cooling': 'hvac',
            'air_quality': 'hvac',
            'co2': 'hvac',
            'humidity': 'hvac',

            'smoke': 'safety',
            'fire': 'safety',
            'motion': 'safety',
            'security': 'safety',
            'alarm': 'safety',
            'gas': 'safety',

            'water': 'plumbing',
            'leak': 'plumbing',
            'plumbing': 'plumbing',
            'moisture': 'plumbing',
            'drain': 'plumbing',

            'power': 'electrical',
            'electrical': 'electrical',
            'voltage': 'electrical',
            'energy': 'electrical',

            'equipment': 'equipment',
            'machine': 'equipment',
            'device': 'equipment'
        };
        for (var key in categoryMap) {
            if (type.indexOf(key) !== -1) {
                return categoryMap[key];
            }
        }
		return 'other';
    }
})(current, previous);
