sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    if (current.assignment_group.nil() || !current.assigned_technician.nil()) {
        return;
    }

    gs.info('BR Fired for maintenance task: ' + current.getDisplayValue());

    var groupId = current.getValue('assignment_group');
    gs.info('Assignment Group ID: ' + groupId);

    var techProfile = new GlideRecord('x_1850913_facili_0_technician_profile'); // Update with your actual table name
    techProfile.addQuery('group', groupId); // Reference field to sys_user_group
    techProfile.addQuery('user.active', 'true'); // Make sure user is active
    techProfile.query();

    var availableTechs = [];

    gs.info('Searching for technicians in group...');

    while (techProfile.next()) {
        var userId = techProfile.getValue('user'); // Get the sys_id of the user reference field
        if (userId) {
            availableTechs.push(userId);
            gs.info('Found technician: ' + techProfile.user.getDisplayValue() + ' (Skill: ' + techProfile.getValue('skill_rate') + ')');
        }
    }

    gs.info('Total available technicians: ' + availableTechs.length);


    var randomIndex = Math.floor(Math.random() * availableTechs.length);
    var selectedTechId = availableTechs[0];

    current.setValue('assigned_technician', selectedTechId);

    var selectedUser = new GlideRecord('sys_user');
    if (selectedUser.get(selectedTechId)) {
        gs.info('Randomly assigned technician: ' + selectedUser.getDisplayValue() +
            ' (Selected ' + (randomIndex + 1) + ' of ' + availableTechs.length + ')');
    }

})(current, previous);
