sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	if (current.assigned_technician){
        return;
	}

	var minSkill = 0;

	switch(current.priority + ""){
		case '1': 
			minSkill = 8;
			break;
		case '2':
			minSkill = 6;
			break;
		case '3':
			minSkill = 4;
			break;
		default:
			minSkill = 0;
	}


	var tech = new GlideRecord('x_1850913_facili_0_technician_profile');
	tech.addQuery('group', current.assignment_group);
    tech.addQuery('skill_rate', '>=', minSkill);
    tech.orderByDesc('skill_rate');
    tech.query();


	while (tech.next()) {

        //Availability check
        var issue = new GlideRecord('x_1850913_facili_0_facility_issue');
        issue.addQuery('assigned_technician', tech.user);
        issue.addQuery('state', 'NOT IN', '6,7'); // closed
        issue.setLimit(1);
        issue.query();

        if (!issue.hasNext()) {
            current.setValue('assigned_technician', tech.user);
            return;
        }
    }

})(current, previous);
