sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    var sensor = current.sensor.getRefRecord();
    if (sensor) {
        var sensorType = sensor.getValue('sensor_type');
        gs.info('Sensor Type is ' + sensorType);
        switch (sensorType) {
            case 'equipment':
			case 'machine':
			case 'device':
                current.setValue('assignment_group', '2e9d78da83c67a104acac396feaad3bb');
                break;
            case 'smoke':
            case 'gas':
            case 'fire':
            case 'motion':
                current.setValue('assignment_group', 'c49d38da83c67a104acac396feaad37a');
                break;
             case 'water_leak':
            case 'water':
                current.setValue('assignment_group', '091715eb83bd32104acac396feaad3f9');
                break;
           case 'temperature':
            case 'humidity':
                current.setValue('assignment_group', '6df651eb83bd32104acac396feaad34d');
                break;
           case 'power':
            case 'light':
                current.setValue('assignment_group', '1e07d1eb83bd32104acac396feaad314');
                break;
            default:
                current.setValue('assignment_group', '8fadf8da83c67a104acac396feaad306')
        }

    }

})(current, previous);
