sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var issueNum = current.getValue('number');
    var techID = current.getValue('assigned_technician');
    var finalState = current.getValue('state');

	gs.info("FACILITY LOG: Closure detected for " + issueNum);

    // 1. UPDATE THE HISTORY RECORD
    var history = new GlideRecord('x_1850913_facili_0_alert_history_table');
    history.addQuery('issue_no', issueNum); 
    history.query();

    if (history.next()) {
        // Set the status based on how it was closed
        var status = (finalState == '3') ? 'Completed' : 'Incomplete'; // 3 = Closed Complete
        history.state = status;
        history.update();
        gs.info("FACILITY LOG: Updated History Record to " + status);
    } else {
        gs.error("FACILITY LOG: Could not find history record for " + issueNum);
        return; // Stop if there is no history to count
    }

    // 2. FIND TECH PROFILE
    var techProfile = new GlideRecord('x_1850913_facili_0_technician_profile');
    if (techProfile.get('user', techID)) {

        var currentSkill = parseFloat(techProfile.skill_rate) || 0;
        var newSkill = currentSkill;

        if (history.state == 'Completed') {
            // 3. COUNT COMPLETED HISTORY
            var countGA = new GlideAggregate('x_1850913_facili_0_alert_history_table');
            countGA.addQuery('assigned_to', techID);
            countGA.addQuery('state', 'Completed');
            countGA.addAggregate('COUNT');
            countGA.query();

            var total = 0;
            if (countGA.next()) {
                total = countGA.getAggregate('COUNT');
            }
            gs.info("FACILITY LOG: Total Completed in History: " + total);

            if (total > 0 && total % 3 == 0) {
                newSkill += 0.5;
                gs.info("FACILITY LOG: Milestone hit! Adding 0.5");
            }
        } else {
            // DEDUCT FOR INCOMPLETE
            newSkill -= 0.2;
            gs.info("FACILITY LOG: Deducting 0.2 for Incomplete");
        }

        // 4. APPLY BOUNDS AND SAVE
        if (newSkill > 10) newSkill = 10;
        if (newSkill < 0) newSkill = 0;

        if (newSkill != currentSkill) {
            techProfile.skill_rate = newSkill;
            techProfile.update();
            gs.info("FACILITY LOG: Skill updated to " + newSkill);
        }
    }

})(current, previous);
