sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {TransformApiHelpers} params.helpers
  */
function evaluateProperty({api, helpers}) {
   const thisWeek = Number(api.data.look_up_multiple_records_2.results.0);
  const lastWeek = Number(api.data.look_up_multiple_records_3.output.data.GlideAggregate_Query[0].count);

  if(lastWeek === 0){
    return 'upload_double-arrow-up-14221.svg';
  }

  if(thisWeek === lastWeek || thisWeek === 0 || lastWeek === 0){
    return 'upload_light-yellow-circle-sun-16985.svg';
  }

  if(thisWeek > lastWeek){
    return 'upload_double-arrow-up-14221.svg';

  }else{
    return'upload_double-arrow-down-14216.svg';
  }
}
