sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {TransformApiHelpers} params.helpers
  */
function evaluateProperty({api, helpers}) {
  const thisWeek = Number(api.data.aggregation_query_4.output.data.GlideAggregate_Query[0].count);
  const lastWeek = Number(api.data.aggregation_query_3.output.data.GlideAggregate_Query[0].count);

   if (lastWeek === 0) {
    return 0 + " %";
  }

  const percentageChange =
    ((thisWeek - lastWeek) / lastWeek) * 100;
  return Number(percentageChange.toFixed(1)) + " %";
}
