sysauto_script.script:
(function () {

    var issueGR = new GlideRecord('x_1850913_facili_0_facility_issue');
    issueGR.addQuery('assigned_technician', '');
    issueGR.addNotNullQuery('assignment_group');
    issueGR.addQuery('state', 'NOT IN', '6,7'); // Not closed
    issueGR.orderBy('priority');
    issueGR.orderBy('sys_created_on');
    issueGR.query();

    while (issueGR.next()) {

        var minSkill = 0;

        switch (issueGR.priority + '') {
            case '1':
                minSkill = 8;
                break;
            case '2':
                minSkill = 6;
                break;
            case '3':
                minSkill = 4;
                break;
            default:
                minSkill = 0;
        }

        var tech = new GlideRecord('x_1850913_facili_0_technician_profile');
        tech.addQuery('group', issueGR.assignment_group);
        tech.addQuery('skill_rate', '>=', minSkill);
        tech.orderByDesc('skill_rate');
        tech.query();

        while (tech.next()) {

            var activeIssue = new GlideRecord('x_1850913_facili_0_facility_issue');
            activeIssue.addQuery('assigned_technician', tech.user);
            activeIssue.addQuery('state', 'NOT IN', '6,7');
            activeIssue.setLimit(1);
            activeIssue.query();

            if (!activeIssue.hasNext()) {
                issueGR.assigned_technician = tech.user;
                issueGR.work_notes = 'Auto-assigned from queue by scheduler';
                issueGR.update();
                break; // assign ONE issue per run
            }
        }

        break; // one incident per execution (important)
    }

})();
